import { Component } from '@angular/core';
import {Category, categories} from "../categories";

@Component({
  selector: 'app-categoty-list',
  templateUrl: './categoty-list.component.html',
  styleUrls: ['./categoty-list.component.css']
})
export class CategotyListComponent {
  categories = categories

  currentCategory = categories[0];

  changeCategory(cat : Category){
    this.currentCategory = cat
  }


}
<h2>Products</h2>

<h3>Categories</h3>

<div *ngFor=" let category of categories">
    <button class="category_button" (click)="changeCategory(category)">{{category.name}}</button>
</div>


<app-product-list [products]="currentCategory.products">
</app-product-list>
